drop table board;
drop table comment;
drop table files;
drop table recipe;
drop table recipe_link;
*/

CREATE TABLE user (
userId varchar(20) not null primary key unique,
userPwd varchar(200) not null,
userNickname varchar(20) not null unique,
userEmail varchar(50) not null unique,
userAuth varchar(50) null,
regdate datetime null,
modifiedDate datetime null
);

CREATE TABLE board (
  bno int NOT NULL AUTO_INCREMENT,
  title varchar(100) COLLATE utf8_bin NOT NULL,
  content text COLLATE utf8_bin NOT NULL,
  writer varchar(30) COLLATE utf8_bin NOT NULL,
  view_cnt int DEFAULT '0',
  comment_cnt int DEFAULT '0',
  reg_date datetime DEFAULT CURRENT_TIMESTAMP,
  up_date datetime DEFAULT CURRENT_TIMESTAMP,
  level varchar(45) COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (bno)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8mb3 COLLATE=utf8_bin;

CREATE TABLE comment (
  cno int NOT NULL AUTO_INCREMENT,
  bno int NOT NULL,
  pcno int DEFAULT NULL,
  comment varchar(3000) DEFAULT NULL,
  commenter varchar(20) DEFAULT NULL,
  reg_date datetime DEFAULT CURRENT_TIMESTAMP,
  up_date datetime DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (cno),
  foreign key(commenter) references user(userId)
);

CREATE TABLE files (
  fno int(11) NOT NULL COMMENT '파일테이블키',
  bno varchar(45) DEFAULT NULL COMMENT '게시글번호',
  file_name varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '파일명',
  file_ext varchar(45) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '파일확장자',
  file_path varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '파일 경로',
  file_savename varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '파일 저장명',
  file_size int(11) DEFAULT NULL COMMENT '파일사이즈',
  PRIMARY KEY (fno)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE recipe (
no int not null primary key,
menu varchar(50),
how varchar(50),
kind varchar(10),
kcal varchar(10),
carbohydrate varchar(10),
protein varchar(10),
fat varchar(10),
natrium varchar(10),
material varchar(500),
recipe1 varchar(150),
recipe2 varchar(150),
recipe3 varchar(150),
recipe4 varchar(150),
recipe5 varchar(150),
recipe6 varchar(150),
recipe7 varchar(150),
recipe8 varchar(150),
recipe9 varchar(150),
recipe10 varchar(150)
);

CREATE TABLE recipe_link (
no int not null primary key,
menuImg varchar(100),
link1 varchar(100),
link2 varchar(100),
link3 varchar(100),
link4 varchar(100),
link5 varchar(100),
link6 varchar(100),
link7 varchar(100),
link8 varchar(100),
link9 varchar(100),
link10 varchar(100),
foreign key(no) references recipe(no)
);

                            
